tables.table_schema:
CREATE TABLE field_yield_data (
    field_id INT PRIMARY KEY,
    season VARCHAR(50),
    crop_type VARCHAR(50),
    yield_amount DECIMAL(10, 2)
);

CREATE TABLE field_productivity (
    field_id INT PRIMARY KEY,
    season VARCHAR(50),
    crop_type VARCHAR(50),
    harvest_duration INT
);

CREATE TABLE fields (
  field_id INT PRIMARY KEY,
  field_name VARCHAR(50),
  field_location VARCHAR(100),
  field_size FLOAT
);

CREATE TABLE field_data (
    field_id UUID PRIMARY KEY,
    season INT,
    crop_type TEXT,
    yield FLOAT
);

CREATE TABLE field_location (
    field_id UUID PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE season_data (
    season INT PRIMARY KEY,
    start_date DATE,
    end_date DATE
);

CREATE TABLE crop_type (
    crop_id UUID PRIMARY KEY,
    crop_name TEXT,
    yield_unit TEXT
);

CREATE TABLE Field (field_id UUID PRIMARY KEY,
field_name VARCHAR(100) NOT NULL,
field_location VARCHAR(100) NOT NULL);

CREATE TABLE Crop (crop_id UUID PRIMARY KEY,
crop_name VARCHAR(100) NOT NULL,
crop_type VARCHAR(100) NOT NULL);
