tables.table_schema:
CREATE TABLE SoilSamples (
  sample_id SERIAL PRIMARY KEY,
  location VARCHAR(100),
  nutrient_level NUMERIC,
  sample_date DATE
);

CREATE TABLE FertilizerTypes (
  fertilizer_id SERIAL PRIMARY KEY,
  name VARCHAR(50),
  nutrient_components JSONB
);

CREATE TABLE FarmFields (
  field_id SERIAL PRIMARY KEY,
  field_name VARCHAR(50),
  area NUMERIC,
  soil_type VARCHAR(50)
);

CREATE TABLE Field (
    Field_ID INT PRIMARY KEY,
    Field_Name VARCHAR(50) NOT NULL,
    Location VARCHAR(100) NOT NULL
);

CREATE TABLE Nutrient (
    Nutrient_ID INT PRIMARY KEY,
    Nutrient_Name VARCHAR(50) NOT NULL,
    Recommended_Level FLOAT NOT NULL
);
