tables.table_schema:
CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE crop_growth_data (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    growth_stage VARCHAR(255),
    growth_rate FLOAT
);

CREATE TABLE predictive_analytics (
    id INT PRIMARY KEY,
    weather_id INT,
    crop_id INT,
    prediction_score FLOAT
);

CREATE TABLE weather_data (
    date DATE NOT NULL,
    temperature DECIMAL NOT NULL,
    humidity DECIMAL NOT NULL
);

CREATE TABLE crop_data (
    crop_name VARCHAR(255) NOT NULL,
    growth_stage VARCHAR(50) NOT NULL,
    planting_date DATE NOT NULL
);

CREATE TABLE weather_forecast (
    date DATE NOT NULL,
    forecasted_temperature DECIMAL NOT NULL,
    forecasted_humidity DECIMAL NOT NULL
);

CREATE TABLE growth_prediction (
    crop_name VARCHAR(255) NOT NULL,
    predicted_growth_stage VARCHAR(50) NOT NULL,
    predicted_harvest_date DATE NOT NULL
);

CREATE TABLE weather_conditions (
    date DATE NOT NULL,
    weather_condition VARCHAR(100) NOT NULL,
    wind_speed DECIMAL NOT NULL,
    precipitation DECIMAL NOT NULL
);
