tables.table_schema:
CREATE TABLE irrigation_schedule (
    id INT PRIMARY KEY,
    date DATE,
    time TIME,
    duration INTERVAL,
    sensor_id INT
);

CREATE TABLE water_usage_data (
    id INT PRIMARY KEY,
    date DATE,
    time TIME,
    gallons_used DECIMAL,
    sensor_id INT
);

CREATE TABLE sensor_data (
    id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    location VARCHAR(100),
    type VARCHAR(50)
);
