tables.table_schema:
CREATE TABLE market_trends (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    market_price DECIMAL(10, 2),
    sales_volume INT
);

CREATE TABLE investment_data (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    investment_amount DECIMAL(10, 2),
    investor_name VARCHAR(255)
);

CREATE TABLE MarketAnalysis (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    market_price DECIMAL(10, 2),
    sales_count INT
);

CREATE TABLE InvestmentData (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    investment_amount DECIMAL(15, 2),
    investor_name VARCHAR(100)
);

CREATE TABLE CropSales (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    sale_date DATE,
    sale_price DECIMAL(10, 2)
);

CREATE TABLE MarketTrends (
    id INT PRIMARY KEY,
    trend_date DATE,
    price_index DECIMAL(8, 2),
    trend_description TEXT
);

CREATE TABLE VendorDetails (
    id INT PRIMARY KEY,
    vendor_name VARCHAR(100),
    contact_number VARCHAR(20),
    location VARCHAR(255)
);
