tables.table_schema:
CREATE TABLE field_boundary_data (
  field_id INT PRIMARY KEY,
  field_name VARCHAR(50),
  boundary_geojson JSONB
);
CREATE TABLE crop_rotation_schedule (
  rotation_id INT PRIMARY KEY,
  crop_name VARCHAR(50),
  planting_date DATE,
  harvesting_date DATE
);

CREATE TABLE FieldBoundaryData (
    field_id UUID PRIMARY KEY,
    field_name TEXT NOT NULL,
    crop_type TEXT NOT NULL
);

CREATE TABLE CropRotationSchedule (
    rotation_id UUID PRIMARY KEY,
    crop_type TEXT NOT NULL,
    rotation_years INT NOT NULL
);

CREATE TABLE PlantingHarvesting (
    event_id UUID PRIMARY KEY,
    event_type TEXT NOT NULL,
    event_date DATE NOT NULL
);

CREATE TABLE FieldBoundaryData (
    field_id UUID PRIMARY KEY,
    field_name VARCHAR(255),
    boundary_data JSON
);

CREATE TABLE CropRotationSchedules (
    crop_id UUID PRIMARY KEY,
    crop_name VARCHAR(255),
    plant_date DATE,
    harvest_date DATE
);

CREATE TABLE PlantingEquipment (
    equipment_id UUID PRIMARY KEY,
    equipment_name VARCHAR(255),
    equipment_type VARCHAR(100),
    field_id UUID REFERENCES FieldBoundaryData(field_id)
);

CREATE TABLE HarvestingEquipment (
    equipment_id UUID PRIMARY KEY,
    equipment_name VARCHAR(255),
    equipment_type VARCHAR(100),
    field_id UUID REFERENCES FieldBoundaryData(field_id)
);

CREATE TABLE FieldBoundaryData (
    id SERIAL PRIMARY KEY,
    field_name TEXT,
    boundary_geometry GEOMETRY,
    created_at TIMESTAMP
);

CREATE TABLE CropRotationSchedule (
    id SERIAL PRIMARY KEY,
    crop_name TEXT,
    planting_date DATE,
    harvest_date DATE
);

CREATE TABLE FieldManagement (
    id SERIAL PRIMARY KEY,
    field_id INT,
    management_type TEXT,
    management_date DATE
);

CREATE TABLE FieldEquipment (
    id SERIAL PRIMARY KEY,
    equipment_name TEXT,
    equipment_type TEXT
);

CREATE TABLE HarvestData (
    id SERIAL PRIMARY KEY,
    crop_id INT,
    yield FLOAT,
    harvest_date DATE
);
