tables.table_schema:
CREATE TABLE satellite_images (
    image_id UUID PRIMARY KEY,
    image_url TEXT,
    date_taken DATE
);

CREATE TABLE crop_health (
    crop_id UUID PRIMARY KEY,
    image_id UUID,
    health_status TEXT,
    area_covered FLOAT
);

CREATE TABLE areas_of_concern (
    concern_id UUID PRIMARY KEY,
    crop_id UUID,
    location TEXT,
    concern_type TEXT
);

CREATE TABLE SatelliteImages (
  image_id UUID PRIMARY KEY,
  image_location STRING,
  capture_date DATE,
  satellite_provider STRING,
);

CREATE TABLE FieldData (
  field_id UUID PRIMARY KEY,
  field_name STRING,
  location STRING,
  crop_type STRING,
);

CREATE TABLE HealthMetrics (
  metric_id UUID PRIMARY KEY,
  image_id UUID REFERENCES SatelliteImages(image_id),
  health_score FLOAT,
  issue_detected BOOLEAN,
);

CREATE TABLE CropAlerts (
  alert_id UUID PRIMARY KEY,
  field_id UUID REFERENCES FieldData(field_id),
  alert_date DATE,
  alert_message STRING,
);

CREATE TABLE UserSubscriptions (
  subscription_id UUID PRIMARY KEY,
  user_id UUID,
  field_id UUID REFERENCES FieldData(field_id),
);
