tables.table_schema:
CREATE TABLE SensorData (
    sensor_id INT PRIMARY KEY,
    data_timestamp TIMESTAMP,
    data_value DECIMAL
);

CREATE TABLE sensor_data (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(50),
    timestamp TIMESTAMP,
    value DECIMAL
);

CREATE TABLE sensor_data (
    sensor_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    value FLOAT NOT NULL
);

CREATE TABLE sensor_metadata (
    sensor_id SERIAL PRIMARY KEY,
    sensor_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE sensor_logs (
    log_id SERIAL PRIMARY KEY,
    sensor_id SERIAL REFERENCES sensor_data(sensor_id),
    log_message TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE SensorHistory (
    id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    data_value FLOAT
);

CREATE TABLE SensorStatus (
    sensor_id INT PRIMARY KEY,
    status VARCHAR(20),
    last_updated TIMESTAMP
);

CREATE TABLE SensorErrors (
    id INT PRIMARY KEY,
    sensor_id INT,
    error_message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE Sensors (
  sensor_id INT PRIMARY KEY,
  location VARCHAR,
  data_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE Temperature (
  temperature_id INT PRIMARY KEY,
  sensor_id INT,
  temperature_value FLOAT,
  FOREIGN KEY (sensor_id) REFERENCES Sensors(sensor_id)
);

CREATE TABLE Humidity (
  humidity_id INT PRIMARY KEY,
  sensor_id INT,
  humidity_value FLOAT,
  FOREIGN KEY (sensor_id) REFERENCES Sensors(sensor_id)
);

CREATE TABLE Pressure (
  pressure_id INT PRIMARY KEY,
  sensor_id INT,
  pressure_value FLOAT,
  FOREIGN KEY (sensor_id) REFERENCES Sensors(sensor_id)
);
