tables.table_schema:
CREATE TABLE sensor_data (
    sensor_id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    timestamp TIMESTAMP,
    value FLOAT
);

CREATE TABLE sensor_data (
    sensor_id INT,
    time_stamp TIMESTAMP,
    value FLOAT
);

CREATE TABLE sensor_aggregated_data (
    sensor_id INT,
    date DATE,
    total_value FLOAT
);

CREATE TABLE sensor_summary (
    date DATE,
    total_sensors INT
);

CREATE TABLE Sensors (
    sensor_id UUID PRIMARY KEY,
    sensor_type VARCHAR(50),
    sensor_location VARCHAR(100)
);

CREATE TABLE SensorData (
    data_id UUID PRIMARY KEY,
    sensor_id UUID REFERENCES Sensors(sensor_id),
    timestamp TIMESTAMP,
    value FLOAT
);

CREATE TABLE SensorAggregatedData (
    aggregated_id UUID PRIMARY KEY,
    sensor_id UUID REFERENCES Sensors(sensor_id),
    aggregation_timestamp TIMESTAMP,
    aggregated_value FLOAT
);

CREATE TABLE SensorAggregatedSummary (
    summary_id UUID PRIMARY KEY,
    aggregation_timestamp TIMESTAMP,
    total_value FLOAT
);

CREATE TABLE sensors_data (
  sensor_id UUID,
  sensor_type VARCHAR,
  timestamp TIMESTAMP,
  value FLOAT
);
