tables.table_schema:
CREATE TABLE sensor_readings (
    id INT PRIMARY KEY,
    sensor_id INT,
    reading_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    sensor_id INT,
    alert_message TEXT,
    alert_timestamp TIMESTAMP
);

CREATE TABLE sensors (
    sensor_id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    sensor_location VARCHAR(50),
    sensor_reading FLOAT
);

CREATE TABLE triggers (
    trigger_id INT PRIMARY KEY,
    trigger_name VARCHAR(50),
    trigger_condition VARCHAR(100),
    alert_recipients VARCHAR(100)
);

CREATE TABLE sensor_readings (
    reading_id INT PRIMARY KEY,
    sensor_id INT REFERENCES sensors(sensor_id),
    reading_value FLOAT,
    reading_timestamp TIMESTAMP
);

CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    trigger_id INT REFERENCES triggers(trigger_id),
    alert_timestamp TIMESTAMP,
    alert_message VARCHAR(200)
);

CREATE TABLE sensor_readings (
  sensor_id INT NOT NULL,
  reading_value FLOAT NOT NULL,
  reading_time TIMESTAMP NOT NULL,
  PRIMARY KEY (sensor_id, reading_time)
);

CREATE TABLE users (
  user_id INT NOT NULL,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL,
  PRIMARY KEY (user_id)
);

CREATE TABLE logs (
  log_id INT NOT NULL,
  log_time TIMESTAMP NOT NULL,
  log_message TEXT NOT NULL,
  PRIMARY KEY (log_id)
);
