tables.table_schema:
CREATE TABLE sensor_data ( 
  sensor_id INT PRIMARY KEY, 
  sensor_name STRING, 
  data_value FLOAT, 
  timestamp TIMESTAMP 
);

CREATE TABLE sensor_metadata ( 
  sensor_id INT PRIMARY KEY, 
  location STRING, 
  calibration FLOAT, 
  last_calibration_date DATE 
);

CREATE TABLE historical_sensor_data (
 sensor_id UUID PRIMARY KEY,
 sensor_type VARCHAR(50),
 sensor_value FLOAT,
 timestamp TIMESTAMP
);

CREATE TABLE sensor_location (
 sensor_id UUID PRIMARY KEY,
 location VARCHAR(100)
);

CREATE TABLE sensor_metadata (
 sensor_id UUID PRIMARY KEY,
 manufacturer VARCHAR(50),
 model VARCHAR(50),
 year_purchased INTEGER
);

CREATE TABLE SensorData (
  sensor_id INT,
  sensor_name TEXT,
  timestamp TIMESTAMP,
  value FLOAT
);

CREATE TABLE SensorLocation (
  sensor_id INT,
  latitude FLOAT,
  longitude FLOAT
);

CREATE TABLE SensorType (
  type_id INT,
  type_name TEXT
);

CREATE TABLE SensorMetadata (
  sensor_id INT,
  metadata_key TEXT,
  metadata_value TEXT
);
