tables.table_schema:
CREATE TABLE investment_decisions (
    date DATE,
    symbol VARCHAR(255),
    decision VARCHAR(255),
    confidence_level INT
);

CREATE TABLE market_indices (
    date DATE,
    index_name VARCHAR(10),
    value DECIMAL(10, 2)
);

CREATE TABLE investment_strategies (
    strategy_id SERIAL PRIMARY KEY,
    strategy_name VARCHAR(50),
    description TEXT
);

CREATE TABLE historical_trends (
    trend_id UUID PRIMARY KEY,
    stock_symbol TEXT,
    trend_date DATE,
    trend_score FLOAT
);

CREATE TABLE investment_strategies (
    strategy_id UUID PRIMARY KEY,
    strategy_name TEXT,
    description TEXT
);

CREATE TABLE portfolio (
    portfolio_id UUID PRIMARY KEY,
    stock_symbol TEXT,
    quantity INT,
    purchase_date DATE
);
