tables.table_schema:
CREATE TABLE DetectedFraud (
    detection_id UUID PRIMARY KEY,
    activity_id UUID,
    detection_time TIMESTAMP,
    detection_result BOOLEAN
);

CREATE TABLE UserProfiles (
    user_id INT PRIMARY KEY,
    user_name TEXT,
    user_email TEXT,
    registration_date TIMESTAMP
);

CREATE TABLE ActionLogs (
    log_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    action_type TEXT,
    action_time TIMESTAMP
);

CREATE TABLE FraudulentActivities (
    activity_id SERIAL PRIMARY KEY,
    activity_type VARCHAR(50) NOT NULL,
    user_id INT NOT NULL,
    activity_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE SuspiciousTransactions (
    transaction_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    transaction_amount DECIMAL NOT NULL,
    transaction_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE UserWatchlist (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    watchlist_reason TEXT
);

CREATE TABLE TransactionHistory (
    history_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    transaction_id INT NOT NULL,
    transaction_status VARCHAR(20) NOT NULL
);

CREATE TABLE FraudulentPatterns (
    pattern_id SERIAL PRIMARY KEY,
    pattern_description TEXT,
    detection_algorithm VARCHAR(50) NOT NULL
);
