tables.table_schema:
CREATE TABLE loyalty_programs (
    user_id INT PRIMARY KEY,
    total_spending DECIMAL,
    loyalty_level VARCHAR
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  user_id INT,
  amount DECIMAL(10, 2),
  timestamp TIMESTAMP
);
