CREATE TABLE currencies (
    currency_id INT PRIMARY KEY,
    currency_code VARCHAR(3) NOT NULL,
    currency_name VARCHAR(50) NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE currencies (
  id SERIAL PRIMARY KEY,
  currency_code VARCHAR(3) NOT NULL,
  country VARCHAR(50) NOT NULL
);

CREATE TABLE transactions (
  transaction_id UUID PRIMARY KEY,
  amount DECIMAL(10,2) NOT NULL,
  currency_id INT REFERENCES currencies(id) NOT NULL,
  transaction_date TIMESTAMP
);

CREATE TABLE exchange_rates (
  from_currency_id INT REFERENCES currencies(id) NOT NULL,
  to_currency_id INT REFERENCES currencies(id) NOT NULL,
  rate DECIMAL(10,5) NOT NULL,
  PRIMARY KEY (from_currency_id, to_currency_id)
);

CREATE TABLE customers (
  customer_id UUID PRIMARY KEY,
  customer_name VARCHAR(100) NOT NULL,
  email VARCHAR(100)
);

CREATE TABLE accounts (
  account_id UUID PRIMARY KEY,
  customer_id UUID REFERENCES customers(customer_id) NOT NULL,
  balance DECIMAL(15,2) NOT NULL
);
