tables.table_schema:
CREATE TABLE supplier_performance_metrics (
  supplier_id INT PRIMARY KEY,
  vendor_name VARCHAR(50),
  delivery_performance_score FLOAT,
  product_quality_score FLOAT
);

CREATE TABLE negotiation_terms (
  vendor_name VARCHAR(50) PRIMARY KEY,
  payment_terms VARCHAR(50),
  contract_duration INT
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    performance_rating INT,
    delivery_performance FLOAT
);

CREATE TABLE Supplier (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(50),
    supplier_location VARCHAR(100)
);

CREATE TABLE PurchaseHistory (
    purchase_id INT PRIMARY KEY,
    supplier_id INT,
    purchase_date DATE,
    purchase_amount DECIMAL(10,2)
);

CREATE TABLE PerformanceMetrics (
    metric_id INT PRIMARY KEY,
    supplier_id INT,
    metric_name VARCHAR(50),
    metric_value DECIMAL(10,2)
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_location VARCHAR(255) NOT NULL
);
