tables.table_schema:
CREATE TABLE customer_queries (
  customer_id INT PRIMARY KEY,
  query_text TEXT
);

CREATE TABLE response_triggers (
  trigger_id INT PRIMARY KEY,
  query_id INT,
  response_text TEXT,
  FOREIGN KEY (query_id) REFERENCES customer_queries(customer_id) ON DELETE CASCADE
);

CREATE TABLE CustomerQueries (
    query_id INT PRIMARY KEY,
    query_text TEXT,
    response_text TEXT
);

CREATE TABLE SQLQueries (
    query_id INT PRIMARY KEY,
    query_text TEXT,
    trigger_id INT
);

CREATE TABLE Triggers (
    trigger_id INT PRIMARY KEY,
    trigger_text TEXT
);

CREATE TABLE ResponsesToQueries (
    id INT PRIMARY KEY,
    query_text VARCHAR(255),
    response_text VARCHAR(255)
);

CREATE TABLE CustomerQueries (
    id INT PRIMARY KEY,
    customer_id INT,
    query_text VARCHAR(255),
    query_date TIMESTAMP
);

CREATE TABLE PredefinedSQLQueries (
    id INT PRIMARY KEY,
    query_text VARCHAR(255),
    response_text VARCHAR(255)
);

CREATE TABLE TriggerLogs (
    id INT PRIMARY KEY,
    trigger_event VARCHAR(255),
    trigger_time TIMESTAMP,
    response_generated BOOLEAN
);

CREATE TABLE CustomerQueryHistory (
    id INT PRIMARY KEY,
    customer_id INT,
    query_id INT,
    query_date TIMESTAMP
);
