tables.table_schema:
CREATE TABLE customer_support_data (
    customer_id INT,
    ticket_id INT,
    issue_description TEXT
);

CREATE TABLE crm_system_data (
    customer_id INT,
    customer_name VARCHAR(50),
    contact_number VARCHAR(15),
    email_address VARCHAR(50)
);

CREATE TABLE customer_journey (
    customer_id INT,
    touchpoint_id INT,
    touchpoint_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE Customer (id INT PRIMARY KEY,
  name VARCHAR,
  email VARCHAR,
  phone VARCHAR,
  address VARCHAR
);

CREATE TABLE SupportTicket (ticket_id INT PRIMARY KEY,
  customer_id INT,
  issue_description TEXT,
  status VARCHAR,
  created_at TIMESTAMP
);

CREATE TABLE CRMSystem (system_id INT PRIMARY KEY,
  system_name VARCHAR,
  integration_key VARCHAR,
  status VARCHAR
);

CREATE TABLE CustomerInteraction (interaction_id INT PRIMARY KEY,
  customer_id INT,
  interaction_type VARCHAR,
  interaction_date TIMESTAMP,
  description TEXT
);

CREATE TABLE CustomerJourney (journey_id INT PRIMARY KEY,
  customer_id INT,
  start_date TIMESTAMP,
  end_date TIMESTAMP,
  journey_details TEXT
);
