tables.table_schema:
CREATE TABLE segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50),
    segment_description TEXT
);

CREATE TABLE segmentations (
    segmentation_id INT PRIMARY KEY,
    segmentation_name STRING,
    criteria STRING
);

CREATE TABLE customer_behavior (
    customer_id INT PRIMARY KEY,
    email STRING,
    purchase_history STRING,
    click_history STRING
);

CREATE TABLE promotional_emails (
    email_id INT PRIMARY KEY,
    email_content STRING,
    segmentation_id INT,
    FOREIGN KEY (segmentation_id) REFERENCES segmentations(segmentation_id)
);

CREATE TABLE segments ( 
    segment_id INT PRIMARY KEY, 
    segment_name VARCHAR(50) 
);

CREATE TABLE behaviors ( 
    behavior_id INT PRIMARY KEY, 
    behavior_name VARCHAR(50) 
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    segmentation VARCHAR(50)
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    customer_id INT,
    order_date DATE,
    total_amount DECIMAL(10, 2),
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    price DECIMAL(10, 2)
);

CREATE TABLE segmentation_rules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255),
    segmentation VARCHAR(50)
);

CREATE TABLE email_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    campaign_type VARCHAR(50),
    segmentation VARCHAR(50)
);
