tables.table_schema:
CREATE TABLE social_media_engagement (
    id INT PRIMARY KEY,
    post TEXT,
    engagement_count INT,
    created_at TIMESTAMP
);

CREATE TABLE marketing_analysis (
    post_id INT,
    engagement_score FLOAT,
    marketing_campaign VARCHAR(255),
    analysis_date DATE
);

CREATE TABLE SocialMediaUsers (
    user_id UUID PRIMARY KEY,
    username STRING,
    followers INT,
    engagement_score FLOAT
);
