tables.table_schema:
CREATE TABLE audit_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE table_1 (
  id INT PRIMARY KEY,
  user_id INT,
  operation VARCHAR(10),
  timestamp TIMESTAMP
);

CREATE TABLE table_2 (
  id INT PRIMARY KEY,
  table_1_id INT,
  accessed_by_user VARCHAR(50),
  access_time TIMESTAMP
);

CREATE TABLE table_3 (
  id INT PRIMARY KEY,
  table_2_id INT,
  operation_type VARCHAR(10),
  operation_time TIMESTAMP
);

CREATE TABLE user_accounts (
    id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    password VARCHAR(50),
    role VARCHAR(20)
);

CREATE TABLE resources (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    type VARCHAR(50),
    description TEXT
);
