tables.table_schema:
CREATE TABLE images (id INT PRIMARY KEY,
    image_url VARCHAR NOT NULL,
    user_id INT NOT NULL,
    upload_date TIMESTAMP NOT NULL,
    caption VARCHAR
);

CREATE TABLE metadata (id INT PRIMARY KEY,
    media_id INT NOT NULL,
    media_type VARCHAR NOT NULL,
    size INT,
    resolution VARCHAR
);

CREATE TABLE images (
  id INT PRIMARY KEY,
  image_url TEXT,
  uploader_id INT,
  upload_date TIMESTAMP
);

CREATE TABLE videos (
  id INT PRIMARY KEY,
  video_url TEXT,
  uploader_id INT,
  upload_date TIMESTAMP
);

CREATE TABLE image_metadata (
  id INT PRIMARY KEY,
  image_id INT,
  metadata_key TEXT,
  metadata_value TEXT
);

CREATE TABLE video_metadata (
  id INT PRIMARY KEY,
  video_id INT,
  metadata_key TEXT,
  metadata_value TEXT
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username TEXT,
  email TEXT
);
