tables.table_schema:
CREATE TABLE marketing_campaign_results (
  customer_id INT PRIMARY KEY,
  response VARCHAR(100) NOT NULL,
  conversion BOOLEAN NOT NULL,
  engagement DECIMAL(5,2) NOT NULL
);

CREATE TABLE Customer_Responses (
    customer_id INT,
    response_date DATE,
    response_text TEXT
);

CREATE TABLE Conversions (
    conversion_id INT,
    customer_id INT,
    conversion_date DATE,
    conversion_value DECIMAL
);

CREATE TABLE Engagement (
    engagement_id INT,
    customer_id INT,
    engagement_date DATE,
    engagement_duration INT
);
