tables.table_schema:
CREATE TABLE Competitors (
	competitor_id INT PRIMARY KEY,
	competitor_name VARCHAR(255),
	product_category VARCHAR(100),
	market_share DECIMAL(5,2)
);

CREATE TABLE MarketTrends (
	trend_id INT PRIMARY KEY,
	trend_description TEXT,
	trend_category VARCHAR(100),
	analysis_date DATE
);

CREATE TABLE CompetitorData (
    competitor_id INT PRIMARY KEY,
    competitor_name VARCHAR(50),
    product_line VARCHAR(100),
    market_share FLOAT
);

CREATE TABLE MarketTrends (
    trend_id INT PRIMARY KEY,
    trend_description TEXT,
    trend_category VARCHAR(50)
);

CREATE TABLE MarketingStrategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    target_audience VARCHAR(100),
    effectiveness_rating FLOAT
);
