SELECT choreography_name, music_name, costume_type FROM choreography_details
JOIN music_details ON choreography_details.music_id = music_details.music_id
JOIN costume_details ON choreography_details.costume_id = costume_details.costume_id;

SELECT choreography_name, music_name, costume_type FROM choreography_details
LEFT JOIN music_details ON choreography_details.music_id = music_details.music_id
RIGHT JOIN costume_details ON choreography_details.costume_id = costume_details.costume_id;

SELECT choreography_name, music_name, costume_type FROM choreography_details
INNER JOIN music_details ON choreography_details.music_id = music_details.music_id
OUTER JOIN costume_details ON choreography_details.costume_id = costume_details.costume_id;
