INSERT INTO troubleshooting_guides (solution, issue_description, troubleshooting_steps, issue_id) VALUES ('Clear Cache', 'App not responding', '1. Go to settings.
2. Select the app.
3. Clear cache.', 2);

INSERT INTO troubleshooting_guides VALUES (3, 'No Internet Connection', 'Reset the router', '1. Unplug the router.
2. Wait for 30 seconds.
3. Plug the router back in.');

INSERT INTO troubleshooting_guides (issue_description, solution, issue_id, troubleshooting_steps) VALUES ('Battery draining quickly', 'Check for background apps', 4, '1. Go to settings.
2. Battery.
3. Check for background apps.');

INSERT INTO troubleshooting_guides (issue_id, issue_description, solution, troubleshooting_steps) VALUES (5, 'Bluetooth not connecting', 'Forget the device', '1. Go to Bluetooth settings.
2. Forget the device.
3. Reconnect the device.');
