statements:
SELECT user_name, user_email
FROM user_details
WHERE user_id = 1;

SELECT subject, sender_email
FROM email_details
WHERE email_id = 1;

SELECT user_name, user_email, subject, sender_email
FROM user_details
INNER JOIN email_details ON user_details.user_id = email_details.email_id;

SELECT user_name, user_email, open_timestamp
FROM user_details
INNER JOIN email_open_rate ON user_details.user_id = email_open_rate.user_id;
