statements:
CREATE TABLE Food (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE Beverage (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    type VARCHAR(50),
    ABV DECIMAL(4,2)
);

CREATE TABLE FoodCategory (
    id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL
);

CREATE TABLE BeverageCategory (
    id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL
);
