tables.table_schema:
CREATE TABLE carbon_footprint (
  id UUID PRIMARY KEY,
  company_name STRING,
  emission_level FLOAT
);

CREATE TABLE energy_consumption (
  id UUID PRIMARY KEY,
  device_id UUID,
  consumption FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE waste_management (
  id UUID PRIMARY KEY,
  company_name STRING,
  waste_type STRING,
  weight FLOAT
);

CREATE TABLE eco_friendly_products (
  id UUID PRIMARY KEY,
  product_name STRING,
  price FLOAT,
  carbon_offset FLOAT
);
