tables.table_schema:
CREATE TABLE ads (
    id INT PRIMARY KEY,
    campaign_id INT,
    ad_content TEXT
);

CREATE TABLE campaigns (
    id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE customers (
    id INT PRIMARY KEY,
    customer_name TEXT,
    email TEXT
);

CREATE TABLE products (
    id INT PRIMARY KEY,
    product_name TEXT,
    price DECIMAL
);

CREATE TABLE interactions (
    id INT PRIMARY KEY,
    customer_id INT,
    ad_id INT,
    interaction_type TEXT
);
