tables.table_schema:
CREATE TABLE LanguageSupport (
    language_code VARCHAR(10) PRIMARY KEY,
    language_name VARCHAR(50) NOT NULL
);

CREATE TABLE Translations (
    translation_id SERIAL PRIMARY KEY,
    key VARCHAR(50) NOT NULL,
    value TEXT NOT NULL,
    language_code VARCHAR(10) REFERENCES LanguageSupport(language_code)
);

CREATE TABLE languages (
    language_id INT PRIMARY KEY,
    language_name VARCHAR(50) UNIQUE
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    preferred_language_id INT REFERENCES languages (language_id)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    description TEXT,
    language_id INT REFERENCES languages (language_id)
);

CREATE TABLE languages (
    language_id UUID PRIMARY KEY,
    language_name VARCHAR(50) NOT NULL
);
