tables.table_schema:
CREATE TABLE suspicious_transactions (
   transaction_id UUID PRIMARY KEY,
   amount DECIMAL,
   transaction_time TIMESTAMP,
   flagged BOOLEAN
);

CREATE TABLE transactions_tracking (
    tracking_id SERIAL PRIMARY KEY,
    transaction_id UUID NOT NULL,
    tracking_status VARCHAR(50) NOT NULL,
    updated_at TIMESTAMP
);

CREATE TABLE flagged_accounts (
    account_id UUID PRIMARY KEY,
    flag_reason TEXT,
    flagged_at TIMESTAMP
);

CREATE TABLE flagged_transactions (
    flag_id SERIAL PRIMARY KEY,
    transaction_id UUID NOT NULL,
    flag_reason TEXT,
    flagged_at TIMESTAMP
);

CREATE TABLE flags (
  flag_id INT PRIMARY KEY,
  flag_reason VARCHAR,
  timestamp TIMESTAMP,
  flagger_id INT
);

CREATE TABLE flaggers (
  flagger_id INT PRIMARY KEY,
  name VARCHAR
);

CREATE TABLE flag_mappings (
  flag_id INT,
  transaction_id INT,
  PRIMARY KEY (flag_id, transaction_id)
);
