tables.table_schema:
CREATE TABLE payment_gateways (
  gateway_id INT PRIMARY KEY,
  gateway_name VARCHAR(255) NOT NULL,
  gateway_type VARCHAR(50) NOT NULL,
  integration_key VARCHAR(100) NOT NULL
);

CREATE TABLE payments (
  payment_id INT PRIMARY KEY,
  amount DECIMAL(10,2) NOT NULL,
  payment_date DATE NOT NULL,
  gateway_id INT REFERENCES payment_gateways(gateway_id)
);

CREATE TABLE payment_transactions (
    transaction_id UUID PRIMARY KEY,
    amount DECIMAL,
    payment_gateway VARCHAR,
    transaction_date TIMESTAMP
);

CREATE TABLE customers (
    customer_id UUID PRIMARY KEY,
    full_name VARCHAR,
    email VARCHAR UNIQUE
);

CREATE TABLE payments (
    payment_id UUID PRIMARY KEY,
    transaction_id UUID REFERENCES payment_transactions(transaction_id),
    customer_id UUID REFERENCES customers(customer_id),
    payment_status VARCHAR
);

CREATE TABLE payment_gateways (
    gateway_id INT PRIMARY KEY,
    gateway_name VARCHAR(50) NOT NULL,
    gateway_url VARCHAR(100) NOT NULL
);

CREATE TABLE payment_transactions (
    transaction_id INT PRIMARY KEY,
    amount DECIMAL(10,2) NOT NULL,
    currency VARCHAR(3) NOT NULL,
    status VARCHAR(20) NOT NULL,
    gateway_id INT,
    FOREIGN KEY (gateway_id) REFERENCES payment_gateways(gateway_id)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    customer_id INT,
    amount DECIMAL(10,2) NOT NULL,
    order_date TIMESTAMP NOT NULL,
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE payments (
    payment_id SERIAL PRIMARY KEY,
    amount DECIMAL,
    currency VARCHAR(3),
    payment_status VARCHAR(20)
);

CREATE TABLE customers (
    customer_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE payment_methods (
    method_id SERIAL PRIMARY KEY,
    method_name VARCHAR(50),
    method_type VARCHAR(50)
);

CREATE TABLE transaction_logs (
    log_id SERIAL PRIMARY KEY,
    transaction_id INT REFERENCES transactions(transaction_id),
    log_message TEXT,
    log_date TIMESTAMP
);
