tables.table_schema:
CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    quantity_in_stock INT,
    last_update TIMESTAMP
);

CREATE TABLE Inventory (
    product_id INT PRIMARY KEY,
    product_name STRING,
    quantity INT,
    location STRING
);

CREATE TABLE Location (
    location_id INT PRIMARY KEY,
    location_name STRING
);
