tables.table_schema:
CREATE TABLE quality_control_measures (
    id INT PRIMARY KEY,
    measure_name VARCHAR(255),
    compliance_level INT
);

CREATE TABLE monitoring_events (
    event_id SERIAL PRIMARY KEY,
    event_timestamp TIMESTAMP,
    event_description TEXT,
    monitoring_status VARCHAR
);

CREATE TABLE quality_control_monitoring (
    id INT PRIMARY KEY,
    product_id INT,
    date DATE,
    issue_description TEXT
);

CREATE TABLE compliance_standards (
    id INT PRIMARY KEY,
    standard_name TEXT,
    description TEXT,
    regulation_id INT
);

CREATE TABLE monitoring_logs (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    action_taken TEXT,
    user_id INT
);

CREATE TABLE quality_control_measures (
    id INT PRIMARY KEY,
    measure_name VARCHAR(255) NOT NULL,
    compliance_status BOOLEAN
);

CREATE TABLE standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE compliance_logs (
    log_id INT PRIMARY KEY,
    monitoring_id INT,
    action_taken TEXT,
    log_date TIMESTAMP
);
