tables.table_schema:
CREATE TABLE customer_preferences (
    customer_id UUID PRIMARY KEY,
    preference JSONB
);

CREATE TABLE recommendations (
    recommendation_id UUID PRIMARY KEY,
    customer_id UUID,
    recommendation_text TEXT,
    CONSTRAINT fk_customer_id FOREIGN KEY (customer_id) REFERENCES customer_preferences(customer_id)
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    product_category VARCHAR(255),
    preference_score INT
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    block_data TEXT
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    product_category VARCHAR(50) NOT NULL,
    preference_score FLOAT NOT NULL
);

CREATE TABLE product_recommendations (
    recommendation_id INT PRIMARY KEY,
    product_id INT NOT NULL,
    customer_id INT NOT NULL,
    recommendation_score FLOAT NOT NULL
);

CREATE TABLE blockchain_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_date DATE NOT NULL,
    transaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE customers_preferences (
    customer_id INT PRIMARY KEY,
    preference_1 VARCHAR(100),
    preference_2 VARCHAR(100),
    preference_3 VARCHAR(100)
);

CREATE TABLE personalized_recommendations (
    recommendation_id INT PRIMARY KEY,
    customer_id INT,
    recommendation_text TEXT,
    date_created TIMESTAMP
);

CREATE TABLE e_commerce_orders (
    order_id SERIAL PRIMARY KEY,
    customer_id INT,
    total_amount DECIMAL(10,2),
    order_date DATE
);

CREATE TABLE product_catalog (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(100),
    price DECIMAL(10,2),
    category VARCHAR(50)
);
