tables.table_schema:
CREATE TABLE blockchain_disputes (
  dispute_id SERIAL PRIMARY KEY,
  dispute_description TEXT,
  resolution_method TEXT,
  blockchain_transaction_id UUID
);

CREATE TABLE resolution_process (
  process_id SERIAL PRIMARY KEY,
  dispute_id INTEGER REFERENCES blockchain_disputes(dispute_id),
  resolution_status TEXT,
  resolution_date TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    role VARCHAR(20)
);

CREATE TABLE resolution_log (
    log_id INT PRIMARY KEY,
    dispute_id INT,
    log_timestamp TIMESTAMP,
    log_details TEXT
);

CREATE TABLE disputes (
	dispute_id UUID PRIMARY KEY,
	dispute_description TEXT,
	dispute_status VARCHAR(50),
	dispute_date TIMESTAMP
);

CREATE TABLE resolutions (
	resolution_id UUID PRIMARY KEY,
	dispute_id UUID,
	resolution_description TEXT,
	resolution_date TIMESTAMP
);

CREATE TABLE blockchain_data (
	block_id UUID PRIMARY KEY,
	transaction_id UUID,
	block_hash VARCHAR(255),
	block_timestamp TIMESTAMP
);

CREATE TABLE users (
	user_id UUID PRIMARY KEY,
	user_name VARCHAR(100),
	user_email VARCHAR(255),
	user_type VARCHAR(50)
);
