tables.table_schema:
CREATE TABLE pricing_information (
  product_id INT,
  price DECIMAL,
  market_trend VARCHAR(50),
  last_updated TIMESTAMP
);

CREATE TABLE market_trends (
  product_id UUID PRIMARY KEY,
  price DECIMAL,
  market_demand INT
);

CREATE TABLE pricing_strategies (
  strategy_id UUID PRIMARY KEY,
  strategy_name VARCHAR,
  formula TEXT
);

CREATE TABLE PricingInformation (
  product_id INT PRIMARY KEY,
  price DECIMAL(10, 2),
  market_trend VARCHAR(50)
);

CREATE TABLE MarketTrends (
  trend_id INT PRIMARY KEY,
  trend_name VARCHAR(50),
  trend_description TEXT
);

CREATE TABLE PricingStrategies (
  strategy_id INT PRIMARY KEY,
  strategy_name VARCHAR(50),
  strategy_details TEXT
);

CREATE TABLE pricing_strategy (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    strategy_description TEXT,
    market_trend VARCHAR(50)
);

CREATE TABLE pricing_history (
    history_id INT PRIMARY KEY,
    product_id INT,
    price DECIMAL(10, 2),
    updated_at TIMESTAMP
);

CREATE TABLE market_trend (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    trend_type ENUM ('positive', 'negative', 'neutral')
);

CREATE TABLE pricing_strategy ( 
   product_id INT, 
   base_price DECIMAL(10,2), 
   market_demand INT, 
   dynamic_price DECIMAL(10,2) 
);

CREATE TABLE product ( 
   product_id SERIAL PRIMARY KEY, 
   product_name VARCHAR(50), 
   category VARCHAR(50), 
   brand VARCHAR(50) 
);

CREATE TABLE market_trend ( 
   trend_id SERIAL PRIMARY KEY, 
   trend_name VARCHAR(50), 
   trend_description TEXT, 
   trend_effect DECIMAL(5,2) 
);

CREATE TABLE pricing_history ( 
   history_id SERIAL PRIMARY KEY, 
   product_id INT, 
   price DECIMAL(10,2), 
   date_updated TIMESTAMP 
);

CREATE TABLE user_activity ( 
   user_id SERIAL PRIMARY KEY, 
   product_id INT, 
   action_type VARCHAR(20), 
   action_date TIMESTAMP 
);
