tables.table_schema:
CREATE TABLE UserBehavior (
  user_id INT,
  email_address VARCHAR(255),
  behavior_type VARCHAR(50),
  behavior_timestamp TIMESTAMP
);

CREATE TABLE EmailMarketingLists (
  list_id INT,
  list_name VARCHAR(100),
  created_by VARCHAR(50),
  creation_date DATE
);

CREATE TABLE user_behavior (
    user_id INT PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    behavior_type VARCHAR(50) NOT NULL,
    behavior_date DATE
);

CREATE TABLE email_list (
    list_id INT PRIMARY KEY,
    list_name VARCHAR(100) NOT NULL,
    segment_criteria VARCHAR(255) NOT NULL
);

CREATE TABLE user_list_mapping (
    map_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    list_id INT NOT NULL,
    FOREIGN KEY (user_id) REFERENCES user_behavior(user_id),
    FOREIGN KEY (list_id) REFERENCES email_list(list_id)
);

CREATE TABLE user_demographics (
  user_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  email VARCHAR(100) UNIQUE
);

CREATE TABLE email_activity (
  email_id INT PRIMARY KEY,
  user_id INT REFERENCES user_demographics(user_id),
  activity_type VARCHAR(20),
  activity_date DATE
);

CREATE TABLE email_campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(100),
  start_date DATE,
  end_date DATE
);

CREATE TABLE user_behaviors (
  behavior_id INT PRIMARY KEY,
  user_id INT REFERENCES user_demographics(user_id),
  behavior_type VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE segment_lists (
  segment_id INT PRIMARY KEY,
  segment_name VARCHAR(100),
  user_behavior_type VARCHAR(50) REFERENCES user_behaviors(behavior_type)
);
