    email_subject VARCHAR(255) NOT NULL,
    email_body TEXT NOT NULL,
    scheduled_time TIMESTAMP NOT NULL,
    segment_id INT,
    FOREIGN KEY (segment_id) REFERENCES customer_segments(segment_id)
);

