tables.table_schema:
CREATE TABLE customer_engagement (
    customer_id INT PRIMARY KEY,
    engagement_score FLOAT,
    engagement_date TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    sentiment_score FLOAT
);

CREATE TABLE brand_perception (
    perception_id INT PRIMARY KEY,
    customer_id INT,
    perception TEXT,
    perception_date TIMESTAMP
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    email VARCHAR,
    location VARCHAR
);

CREATE TABLE likes (
    like_id INT PRIMARY KEY,
    post_id INT,
    customer_id INT
);

CREATE TABLE sentiments (
    sentiment_id INT PRIMARY KEY,
    post_id INT,
    sentiment_score FLOAT,
    sentiment_type VARCHAR
);
