tables.table_schema:
CREATE TABLE inventory ( 
    product_id INT PRIMARY KEY, 
    product_name TEXT, 
    current_stock INT, 
    restock_threshold INT 
);

CREATE TABLE restocking ( 
    restock_id INT PRIMARY KEY, 
    restock_date DATE, 
    product_id INT, 
    restock_quantity INT 
);

CREATE TABLE product_inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    in_stock_qty INT,
    last_restock_date DATE
);

CREATE TABLE restock_history (
    restock_id INT PRIMARY KEY,
    product_id INT,
    restock_date DATE,
    restock_qty INT
);

CREATE TABLE restock_schedule (
    schedule_id INT PRIMARY KEY,
    product_id INT,
    next_restock_date DATE
);

CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    current_stock INT,
    restock_level INT
);

CREATE TABLE sales (
    sale_id INT PRIMARY KEY,
    product_id INT,
    sale_date DATE,
    sale_quantity INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE restock_history (
    restock_id INT PRIMARY KEY,
    product_id INT,
    restock_date DATE,
    restock_quantity INT
);

CREATE TABLE sales_summary (
    summary_id INT PRIMARY KEY,
    sale_id INT,
    product_id INT,
    total_sales DECIMAL(12, 2)
);
