tables.table_schema:
CREATE TABLE transaction_logs (
  log_id INT PRIMARY KEY,
  transaction_id INT,
  log_timestamp TIMESTAMP,
  message TEXT
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    amount DECIMAL,
    currency VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    name VARCHAR,
    email VARCHAR
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR,
    price DECIMAL
);

CREATE TABLE payment_transactions ( transaction_id INT,
                     user_id INT,
                     amount DECIMAL,
                     transaction_date TIMESTAMP
);

CREATE TABLE users ( user_id INT,
                     username VARCHAR(255),
                     email VARCHAR(255),
                     phone_number VARCHAR(15)
);

CREATE TABLE payment_methods ( payment_method_id INT,
                     method_name VARCHAR(255),
                     created_at TIMESTAMP
);

CREATE TABLE payment_status ( status_id INT,
                     status_name VARCHAR(50)
);

CREATE TABLE transaction_log ( log_id INT,
                     transaction_id INT,
                     log_message TEXT,
                     log_timestamp TIMESTAMP
);

CREATE TABLE audit_log ( audit_id INT,
                     user_id INT,
                     action VARCHAR(50),
                     performed_at TIMESTAMP
);
