tables.table_schema:
CREATE TABLE payments (
    id INT PRIMARY KEY,
    payment_data BYTEA
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    encryption_key BYTEA
);

CREATE TABLE payment_information (
    user_id INT,
    card_number VARCHAR(16),
    expiry_date DATE,
    cvv INT
);

CREATE TABLE encryption_keys (
    key_id INT,
    encryption_key VARCHAR(32),
    decryption_key VARCHAR(32)
);

CREATE TABLE encryption_logs (
    log_id INT,
    user_id INT,
    timestamp TIMESTAMP,
    action VARCHAR(50)
);

CREATE TABLE PaymentInformation (
    payment_id INT PRIMARY KEY,
    credit_card_number VARCHAR(16),
    encrypted_security_code VARCHAR(10),
    encrypted_card_holder_name VARCHAR(50)
);

CREATE TABLE EncryptionFunctions (
    function_id INT PRIMARY KEY,
    encryption_type VARCHAR(50),
    encryption_key VARCHAR(100),
    decryption_key VARCHAR(100)
);

CREATE TABLE AuditLog (
    log_id INT PRIMARY KEY,
    log_timestamp TIMESTAMP,
    action_performed VARCHAR(100),
    user_id INT
);

CREATE TABLE UserRoles (
    user_id INT PRIMARY KEY,
    role_name VARCHAR(50)
);
