tables.table_schema:
CREATE TABLE aggregated_transactions (
    aggregated_id UUID PRIMARY KEY,
    total_amount DECIMAL,
    transaction_count INT,
    aggregation_date DATE
);

CREATE TABLE anonymized_transactions (
    transaction_id UUID PRIMARY KEY,
    anonymized_data JSONB
);

CREATE TABLE transaction_data (
    transaction_id INT PRIMARY KEY,
    user_id INT,
    amount DECIMAL,
    merchant_id INT,
    transaction_date TIMESTAMP
);

CREATE TABLE anonymized_data (
    anon_transaction_id INT PRIMARY KEY,
    anon_user_id INT,
    anon_amount DECIMAL,
    anon_merchant_id INT,
    transaction_date TIMESTAMP
);

CREATE TABLE aggregated_data (
    category VARCHAR(50),
    total_amount DECIMAL
);

CREATE TABLE security_measures (
    measure_id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    implementation_date DATE
);
