tables.table_schema:
CREATE TABLE customer_behavior (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    purchase_history TEXT,
    preferences JSON
);

CREATE TABLE shopper_segments (
  shopper_id INT PRIMARY KEY,
  segment_name VARCHAR(50),
  segment_description TEXT,
  total_shoppers INT
);

CREATE TABLE preferences (
    preference_id INT PRIMARY KEY,
    shopper_id INT REFERENCES shoppers(shopper_id),
    preference_name VARCHAR(50),
    preference_description TEXT
);

CREATE TABLE preferences (
  customer_id INT,
  category VARCHAR(50),
  preference_level INT,
  PRIMARY KEY (customer_id, category)
);

CREATE TABLE segments (
  segment_id INT PRIMARY KEY,
  segment_name VARCHAR(50),
  segment_description TEXT
);
