    report_date DATE,
    total_sales DECIMAL,
    products_sold INT
);

CREATE TABLE customer_behavior_report (
    behavior_id INT PRIMARY KEY,
    customer_id INT,
    behavior_description TEXT,
    purchase_history JSONB
);

CREATE TABLE sales_performance_report (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(100),
    sales_amount DECIMAL(10, 2),
    sales_date DATE
);

CREATE TABLE customer_behavior_data (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(100),
    purchased_items INT,
    last_purchase_date DATE
);

CREATE TABLE market_trends_analysis (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(100),
    trend_description TEXT,
    analysis_date DATE
);

CREATE TABLE sales_reports (
	report_id INT PRIMARY KEY,
	report_name VARCHAR(255),
	sales_date DATE,
	sales_amount DECIMAL(10, 2)
);

CREATE TABLE customer_behavior (
	customer_id INT PRIMARY KEY,
