tables.table_schema:
CREATE TABLE feedback (
	sender_id INT,
	receiver_id INT,
	feedback_text TEXT
);

CREATE TABLE products (
	product_id INT,
	product_name VARCHAR(255),
	category VARCHAR(50),
	price DECIMAL(10,2)
);

CREATE TABLE customer_satisfaction_scores (
    satisfaction_id INT PRIMARY KEY,
    customer_id INT,
    overall_satisfaction_score INT,
    feedback_date DATE
);

CREATE TABLE feedback (
  feedback_id INT PRIMARY KEY,
  customer_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE customer (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR,
  email VARCHAR,
  phone_number VARCHAR
);

CREATE TABLE survey (
  survey_id INT PRIMARY KEY,
  customer_id INT,
  survey_question TEXT,
  response TEXT
);
