tables.table_schema:
CREATE TABLE inventory_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    item_quantity INT
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    item_id INT,
    order_date DATE,
    quantity_ordered INT
);

CREATE TABLE Stockouts (
    stockout_id INT PRIMARY KEY,
    item_id INT,
    stockout_date DATE,
    reason VARCHAR(255)
);

CREATE TABLE Overstock (
    overstock_id INT PRIMARY KEY,
    item_id INT,
    overstock_date DATE,
    reason VARCHAR(255)
);
