tables.table_schema:
CREATE TABLE Forecasting_Demand (
    demand_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    demand_date DATE,
    demand_quantity INT
);

CREATE TABLE DemandForecast ( 
   forecast_id VARCHAR(50) PRIMARY KEY, 
   forecast_date DATE, 
   forecast_value FLOAT 
);

CREATE TABLE products (
   product_id INT PRIMARY KEY,
   product_name TEXT,
   product_category TEXT,
   price DECIMAL
);

CREATE TABLE demand_forecast (
    id INT PRIMARY KEY,
    date DATE,
    product_id INT,
    forecasted_quantity INT
);

CREATE TABLE product (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(255),
    price DECIMAL(10, 2)
);

CREATE TABLE demand_forecast (
  id UUID PRIMARY KEY,
  product_name VARCHAR(255),
  forecast_quantity INT,
  forecast_date DATE
);

CREATE TABLE sales_data (
  id UUID PRIMARY KEY,
  product_name VARCHAR(255),
  sales_quantity INT,
  sales_date DATE
);

CREATE TABLE procurement_schedule (
  id UUID PRIMARY KEY,
  product_name VARCHAR(255),
  procurement_quantity INT,
  procurement_date DATE
);

CREATE TABLE demand_analysis (
  id UUID PRIMARY KEY,
  demand_category VARCHAR(255),
  demand_trend VARCHAR(255),
  demand_impact INT
);

CREATE TABLE procurement_analysis (
  id UUID PRIMARY KEY,
  procurement_category VARCHAR(255),
  procurement_trend VARCHAR(255),
  procurement_impact INT
);
